import TextField from "@mui/material/TextField";
import React, { useState } from "react";

const PrimaryUserDataComponent = ({ firstName, lastName, onChange }) => {
  const [dataValid, setDataValid] = useState({
    firstName: false,
    lastName: false,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    const valid = isPrimaryUserDataValid(value);
    setDataValid((prevData) => ({
      ...prevData,
      [name]: valid,
    }));
    if (valid) {
      onChange(event);
    } else {
      onChange({ name: event.name, value: "" });
    }
  };

  const isPrimaryUserDataValid = (value) => {
    const isLengthValid = value.length > 2;
    const containsOnlyLetters = /^[A-Za-z]+$/.test(value);
    return isLengthValid && containsOnlyLetters;
  };

  return (
    <div className="primaryUserData">
      <TextField
        error={!dataValid.firstName}
        id="outlined-required"
        label="First Name"
        onChange={handleChange}
        name="firstName"
        helperText={
          dataValid.firstName
            ? null
            : "Shoud be longer then 2 characters and not include numbers"
        }
      />
      <TextField
        error={!dataValid.lastName}
        id="outlined-required"
        label="Last Name"
        onChange={handleChange}
        name="lastName"
        helperText={
          dataValid.lastName
            ? null
            : "Shoud be longer then 2 characters and not include numbers"
        }
      />
    </div>
  );
};

export default PrimaryUserDataComponent;
