import TextField from "@mui/material/TextField";
import React, { useState } from "react";

const PrimaryUserDataComponent = ({ firstName, lastName, onChange }) => {
  const handleChange = (event) => {
    onChange(event);
  };
  const [text, setText] = useState("");
  const [firstNameValid, setFirstNameValid] = useState(false);
  const [lastNameValid, setLastNameValid] = useState(false);

  const isPrimaryUserDataValid = (value) => {
    const isLengthValid = value.length > 2;
    const containsNumbers = /\d/.test(value);
    return isLengthValid && containsNumbers;
  };

  return (
    <div className="primaryUserData">
      <TextField
        error={text.length >= 3}
        id="outlined-required"
        label="First Name"
        onChange={(e) => setText(e.target.value)}
        name="firstName"
        helperText={firstNameValid ? null : "First name shoud be longer then 2 characters and not include numbers"}
      />
      <TextField
        id="outlined-required"
        label="Last Name"
        onChange={handleChange}
        name="lastName"
      />
    </div>
  );
};

export default PrimaryUserDataComponent;
