import TextField from "@mui/material/TextField";
import React, { useState } from "react";

const SecondaryUserDataComponent = ({ email, password, onChange }) => {
  const [dataValid, setDataValid] = useState({
    email: false,
    password: false,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    if(name === 'email') {
      const valid = isEmailValid(value);
      setDataValid((prevData) => ({
        ...prevData,
        [name]: valid,
      }));
      if (valid) {
        onChange(event);
      }
    } else if(name === 'password') {
      const valid = isPasswordValid(value);
      setDataValid((prevData) => ({
        ...prevData,
        [name]: valid,
      }));
      if (valid) {
        onChange(event);
      }
    }
    
    
  };

  const isEmailValid = (value) => {
    const emailRegex = /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/;
    return emailRegex.test(value);
  };

  const isPasswordValid = (passwordValue) => {
    const hasCapitalLetter = /[A-Z]/.test(passwordValue);
    const hasNumber = /\d/.test(passwordValue);
    const isWithinLengthLimits = passwordValue.length >= 6 && passwordValue.length <= 10;
    return hasCapitalLetter && hasNumber && isWithinLengthLimits;
  };

  return (
    <div className="secondaryUserData">
      <TextField
        error={!dataValid.email}
        id="outlined-required"
        label="Email"
        onChange={handleChange}
        name="email"
        helperText={
          dataValid.email
            ? null
            : "Invalid email format"
        }
      />
      <TextField
        error={!dataValid.password}
        id="outlined-required"
        label="Password"
        onChange={handleChange}
        name="password"
        helperText={
          dataValid.password
            ? null
            : "Password - minimum 1 capital letter, 1 number, maximum 10, minimum 6 characters"
        }
      />
    </div>
  );
};

export default SecondaryUserDataComponent;
