import TextField from "@mui/material/TextField";
import React, { useState } from "react";

const PrimaryUserDataComponent = ({ firstName, lastName, onChange }) => {
  const handleChange = (event) => {
    onChange(event);
  };
  const [text, setText] = useState("");

  const isPrimaryUserDataValid = (value) => {
    const isLengthValid = value.length > 2;
    const containsNumbers = /\d/.test(value);
    return isLengthValid && containsNumbers;
  };

  return (
    <div className="primaryUserData">
      <TextField
        error={text.length >= 3}
        id="outlined-required"
        label="First Name"
        onChange={(e) => setText(e.target.value)}
        name="firstName"
        helperText={text.length >= 3 ? "fsdfsd" : null}
      />
      <TextField
        id="outlined-required"
        label="Last Name"
        onChange={handleChange}
        name="lastName"
      />
    </div>
  );
};

export default PrimaryUserDataComponent;
